// not using keys (not acceptable) <<<< index as key <<<<<<<<< unique id (best practice
import RestaurantCard from "./RestaurantCard";
import restaurantData from "../utils/mockdata";
import { useState,useEffect } from "react";

function filterData(searchText, resList) {
  const filterData = resList.filter((restaurant) =>
    restaurant.data.name.includes(searchText)
  );
  return filterData;
}

const Body = () => {
  const [resList, setRestaurantsData] = useState([]);
  const [searchText, setsearchText] = useState("");


  useEffect(() => {
    getRestaurants();
}, []);


  async function getRestaurants() {
    const data = await fetch("https://www.swiggy.com/dapi/restaurants/list/v5?lat=19.0759837&lng=72.8776559&page_type=DESKTOP_WEB_LISTING");
    const json = await data.json();
    setRestaurantsData(json?.data?.cards[2]?.data?.data?.cards);
}





  return (
    <div className="body">
      <div className="filter">
        <button
          className="filter-btn"
          onClick={() => {
            const UpdateresList = resList.filter(
              (res) => res.data.avgRating > 4
            );
            setresList(UpdateresList);
            //  Whenever react state variable is updated its re render the component
          }}
        >
          TOP Rated Resturants
        </button>
      </div>

      <div className="search-container">
          <input
            className="search-input"
            type="text"
            placeholder="Search"
            value={searchText}
            onChange={(e) => setsearchText(e.target.value)}
          />

          <button
            className="search-btn"
            onClick={() => {
              const data = filterData(searchText, resList);
              setsearchText(data);
            }}
          >
            Search
          </button>
        </div>
      <div className="res-container">
        {resList.map((restaurant) => (
          <RestaurantCard key={restaurant.data.id} resData={restaurant} />
        ))}
      </div>
    </div>
  );
};

export default Body;
