const Body = () => {
  const [resList, setresList] = useState(restaurantData);
  const [searchText, setsearchText] = useState("");
  return (
    <div className="body">
      <div className="filter">
        <button
          className="filter-btn"
          onClick={() => {
            const UpdateresList = resList.filter(
              (res) => res.data.avgRating > 4
            );
            setresList(UpdateresList);
            //  Whenever react state variable is updated its re render the component
          }}
        >
          TOP Rated Resturants
        </button>

        <div className="search-container">
          <input
            className="search-input"
            type="text"
            placeholder="Search"
            value={searchText}
            onChange={(e)=>setsearchText(e.target.value)}
          />

          <button className="search-btn" onClick={() => {
            const data = filterData(searchText,resList);
            setsearchText(data);
          }}>
            Search
          </button>
        </div>
      </div>
      <div className="res-container">
        {resList.map((restaurant) => (
          <RestaurantCard key={restaurant.data.id} resData={restaurant} />
        ))}
      </div>
    </div>
  );
};

export default Body;
