    return (
        <>
            {
                cardsArr.map((card, index) =>
                    <div className="shimmer__card" key={index}>
                        {
                            image ? <div className="shimmer__img"></div> : null
                        }
                        <div className="shimmer__info">
                            {
                                linesArr.map((line, index) =>
                                    <div className="shimmer__line" key={index}></div>
